<style>#preloader_div{height:100vh;width:100%;position:fixed;top:0;right:0;left:0;bottom:0;z-index:9999!important;background-color:var(--cta_primary)}.loader{animation:rotate 1s infinite;height:50px;width:50px}.loader:after,.loader:before{border-radius:50%;content:"";display:block;height:20px;width:20px}.loader:before{animation:ball1 1s infinite;background-color:var(--secondary);box-shadow:30px 0 0 var(--primary);margin-bottom:10px}.loader:after{animation:ball2 1s infinite;background-color:var(--primary);box-shadow:30px 0 0 var(--secondary)}@keyframes rotate{0%{transform:rotate(0) scale(.8)}50%{transform:rotate(360deg) scale(1.2)}100%{transform:rotate(720deg) scale(.8)}}@keyframes ball1{0%{box-shadow:30px 0 0 var(--primary)}50%{box-shadow:0 0 0 var(--primary);margin-bottom:0;transform:translate(15px,15px)}100%{box-shadow:30px 0 0 var(--primary);margin-bottom:10px}}@keyframes ball2{0%{box-shadow:30px 0 0 var(--secondary)}50%{box-shadow:0 0 0 var(--secondary);margin-top:-20px;transform:translate(15px,15px)}100%{box-shadow:30px 0 0 var(--secondary);margin-top:0}}</style><div id="preloader_div"><div class="h-100 w-100 d-flex justify-content-center align-items-center"><span class="loader"></span></div></div><script>const loader=document.getElementById("preloader_div");window.addEventListener("load",(function(){setTimeout(()=>{loader.style.display="none"},1500)}))</script>